/* 
   引入字体
   引入两次的原因是考虑到网络环境问题：
   中国大陆优化：fonts.font.im
   全球/CN优化：fonts.loli.net
*/
@import url('https://fonts.font.im/css2?family=Inter:wght@400;500;700&display=swap');
@import url('https://fonts.font.im/css2?family=Noto+Sans+SC:wght@400;500&display=swap');
@import url('https://fonts.loli.net/css2?family=Inter:wght@400;500;700&display=swap');
@import url('https://fonts.loli.net/css2?family=Noto+Sans+SC:wght@400;500&display=swap');

/* 
    颜色变量定义区，考虑到方便改动，哪怕只有一次调用也通过变量声明
    由于 Waifumode 的视觉设计通用，故在此处不做声明。
*/
:root {
    --main-text-color: rgba(240, 240, 240, 1);        /* 主文本颜色 */
    --border-color: rgba(90, 90, 100, 1);             /* 通用边框色 */
    --blur-tint-color: rgba(35, 35, 40, 1);           /* 模糊背景色 */
    --chat-tint-color: rgba(35, 35, 40, 1);           /* 聊天背景色 */
    --button-hover-bg: rgba(50, 50, 60, 1);            /* 按钮悬停背景 */
    --button-hover-border: rgba(100, 100, 110, 1);    /* 按钮悬停边框 */
    --quote-text-color: rgba(230, 170, 100, 1);       /* 引用文本色 */
    --waifu-quote-text-color-transparent: rgba(230, 170, 100, 0.8); /* Waifumode引用文本发光效果参数 */
    --italics-text-color: rgba(160, 160, 160, 1);     /* 斜体文本色 */
    --code-bg-color: rgba(60, 60, 70, 0.8);           /* 代码块背景 */
    --code-text-shadow: rgba(0, 0, 0, 0.2);              /* 代码块文本阴影 */
    --scrollbar-thumb: var(--border-color);           /* 滚动条滑块, 默认与 border-color 一致 */
    --scrollbar-track: rgba(30, 30, 36);                        /* 滚动条轨道 */
    --scrollbar-thumb-hover-bg: var(--button-hover-border);     /* 滚动条悬停 */
    --waifu-chat-and-scrollbar: rgba(255, 255, 255, 0.25);     /* Waifumode模式滚动条 */
    --bubblechat-mes-block-box-shadow-color: rgba(0, 0, 0, 0.5);      /* 气泡样式聊天箱的阴影 */
    --bubblechat-mes-block-bg: rgba(40, 40, 45, 0.9);
    --bubblechat-mes-is_user-mes_block-bg: rgba(38, 38, 43, 0.9);
    --mes_bias-bg-color: rgba(40, 40, 45, 0.9);
    --menu_button-textarea-hover-box-shadow: rgba(255, 255, 255, 0.3);
    --completion_prompt_manager_prompt-hover-box-shadow: rgba(255, 255, 255, 0.8);
    --mesAvatarWrapper-avatar-box-shadow: rgba(255, 255, 255, 0.7);
    --mesAvatarWrapper-avatar-mes-is_user-border: rgba(90, 90, 100);
    --mesAvatarWrapper-avatar-mes-not_user-border: rgba(255, 255, 255);
    --extensions_settings-box-shadow: rgba(90, 90, 100, 0.65);
    --send_textarea-button-bg: rgba(35, 35, 35, 0.5);
    --send_textarea-button-color: rgba(255, 255, 255, 1);
    --button-universal-hover-box-shadow: rgba(180, 180, 180, 1);
}

/* 通用字体设置 */
body, html {
    font-family: 'Inter', sans-serif; /* 英文部分使用 Inter 字体 */
    line-height: 1.8; /* 增强行间距，提升阅读舒适度 */
    color: var(--main-text-color); /* 使用 main_text_color 作为文本颜色 */
    font-size: 1.02em; /* 增大字体大小，适合长时间阅读 */
}

/* 中文字体设置 */
p, q, h1, h2, h3, h4, h5, h6, i, strong, #send_textarea, .font-box, .formatted-text, * {
    font-family: 'Noto Sans SC', sans-serif; /* 中文部分使用 Noto Sans SC */
}

/* 代码块样式 */
code, .custom-json, .custom-yaml, .hljs {
    font-family: 'Courier New', monospace; /* 使用标准等宽字体 */
    padding: 6px 12px;
    line-height: 1.6;
    border-radius: 8px;
    border: 2px solid var(--border-color); /* 使用 border_color */
    color: var(--SmartThemeBodyColor);
    background: var(--code-bg-color);
    overflow-x: auto;
    text-shadow: 0 1px 3px var(--code-text-shadow);
}

/* 段落文本 */
p {
    font-size: 1.02em; /* 增加字体大小 */
    line-height: 1.8; /* 增加行间距 */
}

/* 引号文本 */
q {
    font-size: 1.02em; /* 增加引用文本的字体大小 */
}

/* 列表项 */
li {
    margin-bottom: 5px;
    font-size: 1.02em; /* 增加字体大小 */
}

/* 标题 */
h1, h2, h3, h4, h5, h6 {
    margin-bottom: 15px;
    font-weight: 700; /* 加大标题字体的加粗效果 */
}

/* 加粗文本 */
strong {
    font-weight: 700; /* 增加加粗的效果 */
}

/* 输入框 */
#send_textarea {
    color: var(--SmartThemeBodyColor);
    background-color: var(--blur-tint-color); /* 使用 blur_tint_color */
    border: 1px solid var(--border-color); /* 使用 border_color */
    border-radius: 6px;
    padding: 12px, 6px;
    resize: none;
    font-size: 0.6em; /* 增加输入框字体大小 */
    margin: 6px 6px; /* 为了与其他元素保持间距 */
}

/* 确保左右元素居中 */
#nonQRFormItems {
    display: flex;
    justify-content: center; /* 居中对齐 */
    align-items: center;     /* 垂直居中 */
    width: 100%;
}

#leftSendForm, #rightSendForm {
    display: flex;
    align-items: center; /* 确保左右元素居中 */
}

#leftSendForm {
    margin-right: 6px; /* 与文本框保持适当的距离 */
}

#rightSendForm {
    margin-left: 6px; /* 与文本框保持适当的距离 */
}

/* 消息气泡样式 */
body.bubblechat .mes_block {
    background-color: var(--bubblechat-mes-block-bg); 
    border-radius: 12px;
    box-shadow: 0 4px 8px var(--bubblechat-mes-block-box-shadow-color); /* 使用 shadow_color */
    color: var(--main-text-color); /* 使用 main_text_color */
    padding: 12px;
    margin: 6px 0;
}

body.bubblechat .mes[is_user="true"] .mes_block {
    background-color: var(--bubblechat-mes-is_user-mes_block-bg); 
    color: var(--main-text-color); /* 使用 main_text_color */
}

/* 按钮样式 */
.menu_button, .text_pole, .textarea {
    background-color: var(--chat-tint-color); /* 使用 chat_tint_color */
    color: var(--main-text-color); /* 使用 main_text_color */
    border: 1px solid var(--border-color); /* 使用 border_color */
    border-radius: 8px;
    padding: 8px 12px;
    cursor: pointer;
    transition: all 0.3s ease;  
    font-size: 0.8em; /* 增加按钮字体大小 */
}

#HotSwapWrapper {
    overscroll-behavior-x: contain;
    scroll-snap-type: x mandatory;
}

/* 修复收藏栏不能横向滑动的问题 */
#HotSwapWrapper .hotswap {
    flex-wrap: nowrap !important;
    overflow-x: auto !important;
    scrollbar-width: none;
    -webkit-overflow-scrolling: touch;
    -ms-overflow-style: none; /* IE/Edge 隐藏滚动条 */
}

#HotSwapWrapper .hotswap:hover {
    scrollbar-width: thin;  /* 鼠标悬停时显示细滚动条 */
}

/* 世界书条目中的深度、顺序、触发 */
input[type="number"][title="深度"].text_pole, input[type="number"][title="顺序"].text_pole, input[type="number"][title="可能性"].text_pole {
    padding: 15px 6px; /* 调整内边距 */
    font-size: 1.0em;
}

/* 文本区域 */
.text_pole, .textarea {
    font-size: 0.9em;
}

.text_pole {
    min-height: fit-content;
}

/* 世界书条目中的状态、位置、深度、顺序、触发的动画*/
.world_entry_form_control.wi-enter-footer-text.flex-container.flexNoGap,
select.text_pole.widthNatural.margin0 {
    transition: box-shadow 0.3s ease;
}

/* 世界书条目中的状态、位置、深度、顺序、触发的选中/聚焦发光效果 */
.world_entry_form_control.wi-enter-footer-text.flex-container.flexNoGap:hover,
.world_entry_form_control.wi-enter-footer-text.flex-container.flexNoGap:focus,
select.text_pole.widthNatural.margin0:hover,
select.text_pole.widthNatural.margin0:focus {
    box-shadow: 0 0 8px var(--menu_button-textarea-hover-box-shadow);
}

/* Hover & active & focus*/
.menu_button:hover, textarea:hover {
    background-color: var(--button-hover-bg); /* 稍微加深背景色 */
    border-color: var(--button-hover-border); /* 增强边框颜色 */
    box-shadow: 0 0 8px var(--menu_button-textarea-hover-box-shadow)
}

/* 预设条目被选中时的效果 */
.completion_prompt_manager_prompt:active,
.completion_prompt_manager_prompt:focus {
    outline: none; /* 移除默认边框 */
    box-shadow: 0 0 10px 5px var(--completion_prompt_manager_prompt-hover-box-shadow); /* 边缘发白光 */
}

/* 预设条目鼠标悬停时的效果 */
.completion_prompt_manager_prompt:hover {
    cursor: pointer; /* 鼠标悬停时显示手形光标 */
    box-shadow: 0 0 10px 3px var(--completion_prompt_manager_prompt-hover-box-shadow); /* 添加光晕效果 */
}  

/* 消息容器部分设置 */
.mes, .last_mes {
    display: flex;
    flex-direction: column;
    margin-bottom: 20px; /* 增加消息的间距 */
}

/* 头像容器设置 */
.mesAvatarWrapper, .last_mes .mesAvatarWrapper {
    display: flex;
    align-items: center;
    justify-content: flex-start;
    margin-bottom: 10px; /* 增加头像与其他内容的间距 */
    padding: 0;
}

/* 头像 */
.mesAvatarWrapper .avatar, .last_mes .mesAvatarWrapper .avatar {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    margin-right: 15px; /* 增加头像与文本的间距 */
    box-shadow: 0 0 6px var(--mesAvatarWrapper-avatar-box-shadow); /* 添加白色光晕效果 */
    align-self: flex-start;
}

#chat .mes[is_user="true"] .avatar {
    border: 2px solid var(--mesAvatarWrapper-avatar-mes-is_user-border);
}

#chat .mes[is_user="false"] .avatar {
    border: 2px solid var(--mesAvatarWrapper-avatar-mes-not_user-border);
}

.mesAvatarWrapper .avatar img, .last_mes .mesAvatarWrapper .avatar img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
}

/* 显示消息的用户名、时间和ID */
.mesAvatarWrapper .mesIDDisplay,
.mesAvatarWrapper .mes_timer,
.mesAvatarWrapper .tokenCounterDisplay,
.last_mes .mesAvatarWrapper .mesIDDisplay,
.last_mes .mesAvatarWrapper .mes_timer,
.last_mes .mesAvatarWrapper .tokenCounterDisplay {
    display: inline-block;
    font-size: 0.8em; /* 让这些元素的字体较小 */
    margin-bottom: 2px;
    margin-left: 3px;
}


/* 主体消息区域 */
.mes_block, .last_mes .mes_block {
    background-color: var(--chat-tint-color); /* 使用 chat_tint_color */
    border-radius: 10px;
    padding: 20px;
    margin-top: 10px;
}

/* 消息的用户名字、时间显示 */
.mes_block .ch_name, .last_mes .mes_block .ch_name {
    display: flex;
    justify-content: space-between;
    margin-bottom: 10px;
    align-items: center;
}

/* 用户名区域 */
.mes_block .ch_name .name_text, .last_mes .mes_block .ch_name .name_text {
    font-weight: 700;
}

/* 消息文本 */
.mes_text q, .last_mes .mes_text q {
    margin-top: 10px; /* 增加引用文本和其他文本的间距 */
}

/* 文字区域 */
.mes_text p, .last_mes .mes_text p {
    margin: 0;
}

.mes_text, .last_mes .mes_text {
    font-size: 1.0em;
}

.mes_text > p {
    margin-bottom: 12px; /* 添加较小的间距，改善阅读性 */
}

.last_mes .mes_text > p {
    margin-bottom: 12px; /* 添加较小的间距，改善阅读性 */
}

/* 仅对 last_mes 的 mes_text 做调整 */
.last_mes .mes_text {
    width: 100%; /* 确保文本区域占满父容器宽度 */
    padding: 5px 0px; /* 确保左右间距适中 */
    box-sizing: border-box; /* 确保 padding 不影响宽度 */
}

.mes_img_controls, .last_mes .mes_img_controls {
    display: flex;
    gap: 10px;
    margin-bottom: 10px;
}

/* 图片内容 */
.mes_img, .last_mes .mes_img {
    max-width: 100%;
    border-radius: 10px;
}

/* 消息底部控制区 */
.mes_bias, .last_mes .mes_bias {
    height: 10px;
    background-color: var(--mes_bias-bg-color);
    border-radius: 8px;
}

/* 消息状态操作区 */
.flex-container.swipeRightBlock, .last_mes .flex-container.swipeRightBlock {
    display: flex;
    justify-content: center;
    margin-top: 10px;
}

/* 去除酒馆的设定按钮的高光+添加发光效果做区分 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
    background-image: none;
    border: 1px solid var(--SmartThemeBorderColor);
    box-shadow: 0px 0px 10px var(--extensions_settings-box-shadow);
}

/*
发送栏圆角，正常情况下不应启用此功能，否则会导致移动端出现漏光问题
#send_form {
    border-radius: 15px;  
}
*/

/* 

    QR 栏，不是 QR 菜单，发送消息上面那个 QR 栏 
    这里是 QR 栏自动隐藏以及动画的实现，但是效果不是很好
    因为 flex 和 none 之间不能动画过渡，使用了 starting-style 和 allow-discrete 才能保持一些动画效果
    同时由于权重问题，只能使用 important，使用 display: none;的原因是不这样做就无法解决布局占用，只能隐藏按钮
    等之后有更好的方法再优化

    从别的主题那里学习了方法，据说是 @Rivelle 老师的方法，天才一样的工程学想法！
    作者太懂什么叫面向用户编程了，我还在苦苦头疼如何移除 QR 栏的布局，他直接把 QR 栏高度设置成 0.1px！选中的时候再展开。
    但这样做有一个缺点就是会导致如果 QR 过多时无法显示全部 QR，只能算是取巧，需要靠滚动查看所有 QR，然而滚动并不好用，
    看看有没有什么更好的改进！

    在其方法基础上稍作修改以符合预期，旧方法作为对 CSS 新特性的探索保留在注释中。

*/

/* QR 悬浮按钮溢出修正 */
#qr--popoutTrigger {
    flex-grow: 1;              
    max-width: 100%;          
    max-height: 100%;          
    display: flex;            
    justify-content: center;  
    align-items: center;       
    padding: 5px;              
}

/* 初始时隐藏 QR 栏 */
#qr--bar {
    max-height: 0px;
    overflow-y: hidden; /* 隐藏超出内容 */
    position: absolute !important; /* 新增定位方式 */ 
    bottom: 100%; /* 相对于父容器定位 */
    left: 0;
    right: 0;
    z-index: 0;
    border-radius: 8px 8px 0 0;
    background: var(--chat-tint-color) !important;
}

/* 鼠标悬停或获得焦点时展开，并显示滚动条 */
#send_form:has(#send_textarea:hover) #qr--bar,
#send_form:has(#send_textarea:focus-within) #qr--bar,
#qr--bar:hover {
    transition: max-height 1.0s ease;   
    max-height: 80px;
    z-index: 100;
    overflow-y: auto; /* 超出时显示滚动条 */
    overflow-y: overlay; 
}

/* 
    悬停或点击发送消息/停止发送按钮时自动收回 QR 栏
*/
#send_form:has(#send_but:hover) #qr--bar,
#send_form:has(#send_but:active) #qr--bar,
#send_form:has(#mes_stop:hover) #qr--bar,
#send_form:has(#mes_stop:active) #qr--bar {
    max-height: 0px;
    transition: max-height 0.5s ease;
}


/* 学来的方法
#qr--bar {    
    height: 0.1px;
}

#send_form:hover #qr--bar {
    height: 40px;    
    transition: all 0.5s ease;
}
*/

/* 旧方法
#qr--popoutTrigger {
    flex-grow: 1;              
    max-width: 100%;          
    max-height: 100%;          
    display: flex;            
    justify-content: center;  
    align-items: center;       
    padding: 5px;              
}


#qr--bar {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
    display: none !important;
    transition: 1.25s allow-discrete;
    @starting-style {
        opacity: 1;
      }
}


#send_form:hover #qr--bar,
#send_form:focus-within #qr--bar {
    opacity: 1;
    visibility: visible;
    pointer-events: all;
    display: flex !important;
    transition: 1.25s allow-discrete;
    @starting-style {
        opacity: 0;
      }
}
*/

#top-settings-holder {
    background-color: #1e1e24;
}

#leftSendForm {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px; /* 为图标之间设置间距 */
    padding-left: 3px;
    padding-right: 0px;
}

/* 圆形的半透明按钮样式 */
#options_button, 
#extensionsMenuButton,
#send_but {
    background-color: var(--send_textarea-button-bg); /* 半透明黑灰色 */
    border-radius: 50%; /* 圆形 */
    width: 45px; /* 固定宽度 */
    height: 45px; /* 固定高度 */
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 10px; /* 内边距，确保图标不紧贴按钮边缘 */
    cursor: pointer;
}

/* 图标大小适配按钮 */
#options_button, 
#extensionsMenuButton,
#send_but {
    font-size: 20px; /* 设置图标的大小，可以根据需要调整 */
    color: var(--send_textarea-button-color); /* 图标颜色，调整为白色 */
}

#rightSendForm {
    display: flex;
    justify-content: center;
    align-items: center;
}

#options_button:hover, 
#extensionsMenuButton:hover,
#send_but:hover,
#qr--bar .menu_button:hover {
    box-shadow: 0 0 8px var(--button-universal-hover-box-shadow);
}

#form_sheld {
    border-top: none;
}

#send_form {
    border: none;
    position: relative;
}

#send_textarea {
    border: none;
    border-radius: 12px 12px 6px 6px;
    font-size: 0.5em;
    /* text-align: center;  文字居中 */
}

#qr--bar .menu_button {
    border: 1px, solid, var(--send_textarea-button-color) !important;
}

/* 滚动条样式 */    
* {
    scrollbar-color: var(--border-color) #1e1e24; /* 使用 border_color 和 chat_tint_color */
    scrollbar-width: thin;
}

::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}

::-webkit-scrollbar-thumb {
    background-color: var(--border-color); /* 使用 border_color */
    border-radius: 6px;
}

::-webkit-scrollbar-thumb:hover {
    background-color: var(--scrollbar-thumb-hover-bg);
}

::-webkit-scrollbar-track {
    background-color: var(--scrollbar-track); 
}

/* 视觉小说(WaifuMode)支持 */
body.waifuMode #chat, 
body.waifuMode #mes_block,
body.waifuMode #mes,
body.waifuMode #last_mes,
body.waifuMode #top-bar,
body.waifuMode #send_form,
body.waifuMode #send_textarea,
body.waifuMode #qr--bar .menu_button,
body.waifuMode #qr--popout .menu_button {
    background-color: rgba(35, 35, 40, 0.15);
}

body.waifuMode * {
    scrollbar-color: var(--waifu-chat-and-scrollbar) rgba(30, 30, 36, 0); /* 使用淡白色滚动条和透明背景 */
    scrollbar-width: thin;
}

body.waifuMode #chat
{
    border-radius: 12px 12px 0 0;
    border-top: var(--waifu-chat-and-scrollbar);
    border-left: var(--waifu-chat-and-scrollbar);
    border-right: var(--waifu-chat-and-scrollbar);
    border-bottom: none;
}

body.waifuMode #top-bar {
    height: 0px;
    border: none;
    box-shadow: none; /* 光晕效果 */
}

body.waifuMode #mes_block {
    box-shadow: 0 0 6px 3px rgba(255, 255, 255, 0.15); /* 添加光晕效果 */
}

body.waifuMode #form_sheld {
    border-top: none;
    padding-bottom: 1%;
}

body.waifuMode #send_form {
    border: 2px solid rgba(255, 255, 255, 0.35);
    border-radius: 8px 8px 8px 8px;
}

body.waifuMode #qr--bar {
    background-color: rgba(35, 35, 40, 0.3) !important;
}

body.waifuMode #qr--popout {
    border: 1px solid rgba(255, 255, 255, 0.55) !important;
    background-color: rgba(35, 35, 40, 0.2);
}

body.waifuMode #send_textarea {
    border: none;
    border-radius: 12px 12px 6px 6px;
    font-size: 0.5em;
}

body.waifuMode #qr--bar .menu_button,
body.waifuMode #qr--popout .menu_button {
    border: 1px, solid, rgba(255, 255, 255, 1) !important;
}

body.waifuMode .mes_block,
body.waifuMode .last_mes .mes_block {
    background-color: rgba(35, 35, 40, 0.3);
}

body.waifuMode .mes_bias, 
body.waifuMode .last_mes .mes_bias {
    background-color: rgba(35, 35, 40, 0.3);
}

/* 初始状态：隐藏 top-settings-holder */
body.waifuMode #top-settings-holder {
    background-color: transparent;
    opacity: 0;
    max-height: 30px;
    pointer-events: all; /* 交互 */
    transition: opacity 1.0s ease, visibility 1.0s ease, max-height 1.0s ease, pointer-events 0s ease;
}

/* 当 #top-settings-holder 悬停时，显示 top-settings-holder */
body.waifuMode #top-settings-holder:hover {
    opacity: 1;
    visibility: visible;
    max-height: 80px;
    pointer-events: auto; /* 交互 */
}

/* 仅在 body.waifuMode 下生效 */
body.waifuMode #leftSendForm {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px; /* 为图标之间设置间距 */
    padding-left: 3px;
    padding-right: 0px;
}

/* 圆形的半透明按钮样式 */
body.waifuMode #options_button, 
body.waifuMode #extensionsMenuButton,
body.waifuMode #send_but {
    background-color: rgba(255, 255, 255, 0.5); /* 半透明白色 */
    border: 1.5px, solid, rgba(255, 255, 255, 0.5);
    border-radius: 50%; /* 圆形 */
    width: 40px; /* 固定宽度 */
    height: 40px; /* 固定高度 */
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 10px; /* 内边距，确保图标不紧贴按钮边缘 */
    cursor: pointer;
}

/* 图标大小适配按钮 */
body.waifuMode #options_button, 
body.waifuMode #extensionsMenuButton,
body.waifuMode #send_but {
    font-size: 20px; /* 设置图标的大小，可以根据需要调整 */
    color: #000; /* 图标颜色，调整为黑色 */
}

/* 仅在 body.waifuMode 下生效 */
body.waifuMode #rightSendForm {
    display: flex;
    justify-content: center;
    align-items: center;
}

body.waifuMode #options_button:hover, 
body.waifuMode #extensionsMenuButton:hover,
body.waifuMode #send_but:hover,
body.waifuMode #qr--bar .menu_button:hover,
body.waifuMode #qr--popout .menu_button:hover {
    box-shadow: 0 0 8px rgba(255, 255, 255, 1);
}

body.waifuMode #qr--bar .menu_button:hover,
body.waifuMode #qr--popout .menu_button:hover {
    background-color: rgba(35, 35, 40, 0.3) !important;
}


/* 仅在 body.waifuMode 下生效 */
/* 引用文本 */
body.waifuMode q {
    color: var(--quote-text-color); /* 使用 quote_text_color */
    font-size: 1.02em; /* 增加引用文本的字体大小 */
    text-shadow: 0 0 1px var(--quote-text-color), 0 0 1px var(--waifu-quote-text-color-transparent) !important; /* 文字边框发光 */
}

/* 加粗文本 */
body.waifuMode strong {
    color: var(--main-text-color); /* 使用 main_text_color */
    font-weight: 700; /* 增加加粗的效果 */
    text-shadow: 0 0 1px rgba(0, 0, 0, 1), 0 0 1px rgba(0, 0, 0, 0.7) !important; /* 文字边框发光 */
}

/* 斜体文本 */
body.waifuMode i, 
body.waifuMode em {
    color: var(--italics-text-color); /* 使用 italics_text_color */
    text-shadow: 0 0 1px rgba(0, 0, 0, 1), 0 0 1px rgba(0, 0, 0, 0.7) !important; /* 文字边框发光 */
}



/* 设置响应式设计 */
@media (max-width: 768px) {
    .mes_block, .menu_button, .text_pole, #send_textarea {
        padding: 8px 12px;
        font-size: 14px;
    }

    .mes_block, .last_mes .mes_block {
        padding: 15px;
    }

    .mes_buttons, .last_mes .mes_buttons {
        flex-direction: column;
        gap: 5px;
    }

    .mes_img_container, .last_mes .mes_img_container {
        margin-top: 10px;
    }

    .mes_text, .last_mes .mes_text {
        font-size: 1.15em;
    }

    #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
        padding-left: 1em;  /* 增加左边距 */
        padding-right: 1em; /* 增加右边距 */
        overflow-y: auto;   /* 启用垂直滚动 */
    }

    /* 对于整个列表容器也进行优化，避免溢出 */
    #completion_prompt_manager #completion_prompt_manager_list {
        padding-left: 1em;  /* 增加左右空白边界 */
        padding-right: 1em;
    }

    /* 正则，这东西的移动端到底是谁设计的，shit! */
    .regex-script-container {
        padding-right: 25px;
    }

    input[type="number"][title="深度"].text_pole, input[type="number"][title="顺序"].text_pole, input[type="number"][title="可能性"].text_pole {
        padding: 8px 4px; /* 调整内边距 */
    }

}

