custom_css:
/* 消息块基础样式 */
#chat .mes .mes_block {
    background-color: rgba(255, 255, 255, 0.1) !important;
    backdrop-filter: blur(4px) !important;
    -webkit-backdrop-filter: blur(4px) !important;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.15);
    border-radius: 15px;
    padding: clamp(10px, 2vw, 25px) clamp(15px, 3vw, 35px); /* 自适应内边距 */
    width: fit-content; /* 自适应宽度 */
}



/* 消息块悬停效果 */
#chat .mes .mes_block:hover {
    background-color: rgba(255, 255, 255, 0.2) !important;
    transition: all 0.3s ease;
}

/* 聊天区域背景 */
#chat {
    backdrop-filter: none !important;
    background-color: transparent !important;
}

/* 表单样式 */
#send_form,
.form-group {
    background: rgba(255, 255, 255, 0.08) !important;
    backdrop-filter: blur(8px) !important;
    -webkit-backdrop-filter: blur(8px) !important;
    border: 1px solid rgba(255, 255, 255, 0.15) !important;
    border-radius: 8px;
    margin: 10px 0;
}

/* 按钮样式 */
.menu_button,
.settings-button,
.btn {
    background: rgba(255, 255, 255, 0.1) !important;
    backdrop-filter: blur(5px) !important;
    -webkit-backdrop-filter: blur(5px) !important;
    border: 1px solid rgba(255, 255, 255, 0.2) !important;
    transition: all 0.3s ease;
}



/* 按钮悬停效果 */
.menu_button:hover,
.settings-button:hover,
.btn:hover {
    background: rgba(255, 255, 255, 0.2) !important;
    transform: translateY(-1px);
}

/* 顶部栏样式 */
#top-bar,
#top-settings-holder {
    background: rgba(255, 255, 255, 0.1) !important;
}

/* 消息气泡背景加深 */
#chat .mes .mes_block {
    background-color: rgba(0, 0, 0, 0.4) !important;
    border: 1px solid rgba(255, 255, 255, 0.15);
}

/* 用户消息样式加深 */
#chat .mes[is_user="true"] .mes_block {
    background-color: rgba(0, 0, 0, 0.4) !important;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

/* 消息块悬停效果加深 */
#chat .mes .mes_block:hover {
    background-color: rgba(0, 0, 0, 0.45) !important;
    transition: all 0.3s ease;
}

/* 输入区域样式加深 */
#send_form,
.form-group {
    background: rgba(0, 0, 0, 0.4) !important;
    backdrop-filter: blur(8px) !important;
    -webkit-backdrop-filter: blur(7px) !important;
    border: 1px solid rgba(255, 255, 255, 0.15) !important;
}

/* 输入框悬停效果 */
#send_form:focus-within,
.form-group:focus-within {
    background: rgba(0, 0, 0, 0.45) !important;
    border: 1px solid rgba(255, 255, 255, 0.25) !important;
}

/* 行内代码样式 */
code {
    background-color: rgba(0, 0, 0, 0.45) !important;
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 4px;
    padding: 2px 6px;
    font-family: monospace;
}

/* 代码块样式 */
pre code,
.code-block {
    background-color: rgba(0, 0, 0, 0.35) !important;
    backdrop-filter: blur(6px) !important;
    -webkit-backdrop-filter: blur(6px) !important;
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-radius: 8px;
    padding: 15px;
    display: block;
    margin: 10px 0;
}

/* 代码块悬停效果 */
pre code:hover,
.code-block:hover {
    background-color: rgba(0, 0, 0, 0.6) !important;
    border-color: rgba(255, 255, 255, 0.2);
    transition: all 0.3s ease;
}

/* 引用文本样式 */
.mes_text q {
    color: #b6c6d6;
    text-shadow: 0 0 2px #512e6b,0 0 4px #6486a1,0 0 6px #6486a1,0 0 8px #6486a1;
}

/* 顶部栏样式加深 */
#top-bar {
    background-color: rgba(0, 0, 0, 0.35) !important;
    backdrop-filter: blur(8px) !important;
    -webkit-backdrop-filter: blur(6px) !important;
    border-bottom: 1px solid rgba(255, 255, 255, 0.15);
    z-index: 1000 !important; /* 確保頂部欄在最上層 */
}

/* 顶部栏悬停效果 */
#top-bar:hover {
    background-color: rgba(0, 0, 0, 0.4) !important;
    transition: all 0.3s ease;
}

/* 顶部栏设置内容 */
#top-bar .settings-menu-wrapper,
#top-bar .dropdown-content {
    display: block !important;
    visibility: visible !important;
    opacity: 1 !important;
    pointer-events: auto !important;
    z-index: 1001 !important; /* 確保設置選單在頂部欄之上 */
}

/* 消息边框重置 */
#chat .mes {
    border: 0 !important;
    background: none !important;
    position: relative !important;
}

/* 聊天区域边框重置 */
#chat {
    background: none !important;
    border: none !important;
}

/* 消息块边框重置 */
#chat .mes,
#chat .mes_block,
#chat .mes_content {
    border-top: 0 !important;
    border-bottom: 0 !important;
    outline: none !important;
    box-shadow: none !important;
}

/* 消息间距 */
#chat .mes + .mes {
    margin-top: 2px !important;
}

/* 复选框样式重置 */
#chat .mes .checkbox::before,
#chat .mes .checkbox::after {
    display: block !important;
    content: initial !important;
}

/* 复选框输入样式重置 */
#chat .mes .checkbox input[type="checkbox"]::before,
#chat .mes .checkbox input[type="checkbox"]::after {
    display: block !important;
    content: initial !important;
}




/* 将重写按钮移到body直接子元素 */
body > .swipe_right {
    position: fixed;
    right: 20px;
    top: 50%;
    transform: translateY(-50%);
    z-index: 9999;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 50%;
    padding: 8px;
    cursor: pointer;
    pointer-events: auto;
    width: 30px;
    height: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* 确保消息文本不受影响 */
#chat .mes_text {
    width: 100% !important;
    padding-right: 0 !important;
}
